public class Exercise4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 100;
		int result;
		
		System.out.println("오 찗像 써쎀 - "+(result=number+10));
		System.out.println("짋 찗像 써쎀 - "+(result=number-10));
		System.out.println("썼쨰 찗像 써쎀 - "+(result=number*10));
		System.out.println("씱얋 찗像 써쎀 - "+(result=number/10));
	}

}
